import React from 'react'
import Slider from 'react-slick'
import { useState } from "react";
import './index.css'
const Content = () => {
  const [imageIndex,setImageIndex]=useState(0)
   let settings ={
    dots:true,
    infinite:true,
    speed:500,
    slidesToShow:1,
    slidesToScroll:1,
    afterChange: (index) => setImageIndex(index),
    adaptiveHeight: true,
   }
  const  images=[
   {
    src: require ('./Assets/img1.jpeg').default
   },
   {
    src:'./Assets/img2.jpeg'
   },
   {
    src:'./Assets/img3.jpeg'
   },
   {
    src:'./Assets/img4.jpeg'
   },
   {
    src:'./Assets/img5.jpeg'
   },
   {
    src:'./Assets/img6.jpeg'
   },
   {
    src:'./Assets/img7.jpeg'
   },
   {
    src:'./Assets/img8.jpeg'
   },
   {
    src:'./Assets/img9.jpeg'
   },
   {
    src:'./Assets/img10.jpeg'
   },
   {
    src:'./Assets/img11.jpeg'
   },
   {
    src:'./Assets/img12.jpeg'
   },
   {
    src:'./Assets/img13.jpeg'
   },
   {
    src:'./Assets/img14.jpeg'
   },
   {
    src:'./Assets/img15.jpeg'
   },
   {
    src:'./Assets/img16.jpeg'
   },
 ]
  return (
    <main>
      <h1>IMAGE SLIDER</h1>
      <div className="container">
        <Slider {...settings}>
        {images.map((image,index) => (

             <div key={index}  className="image">
            <img src={image.src}alt="Slider imgs" />
          </div>

        ))}
        </Slider>
        <div className="image-info">
          Images{imageIndex + 1} of {images.length}
        </div>
        </div>  
    </main>
  )
}

export default Content
